import './App.css';
import factsData from './WitzigeNilsFakten.json';
import { useState, useEffect } from 'react';

function App() {
  const backgroundStyle = {
    backgroundImage: `url('giphy.gif')`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh'
  };
  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Farbe des Overlays (halbtransparentes Schwarz)
  };
  const [facts, setFacts] = useState([]);

  useEffect(() => {
    // Lade die Fakten aus der JSON-Datei beim Komponenten-Render
    setFacts(factsData.fakten);
  }, []);

  return (
    <div className="App" style={backgroundStyle}>
      <div style={overlayStyle}>
        <h1 id='titel' className='stiling'>Witziger Nilsfakt</h1>
        <div className='position'>
          {facts.length > 0 ? (
            facts.map((fact, index) => (
              <div key={index}>
                <p className='stiling' id='quote'>{fact.inhalt}</p>
                <p className='stiling' id='citing'>entelein 20.12.2020</p>
              </div>
            ))
          ) : (
            <p>hihihahahah</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
