import './App.css';
import factsData from './WitzigeNilsFakten.json';
import { useState, useEffect } from 'react';

function App() {
  const backgroundStyle = {
    backgroundImage: `url('giphy.gif')`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh'
  };
  const [facts, setFacts] = useState([]);
  useEffect (() => {
    // Lade die Fakten aus der JSON-Datei beim Komponenten-Render
    setFacts(factsData.fakten);
  }, []);

  return (
    <div className="App" style={backgroundStyle}>

      <h1 id='titel' className='stiling'>Witziger Nilsfakt</h1>
      <div class='position'>
        <p class='stiling' id='quote'>
          {facts.inhalt}</p>
        <p class='stiling' id='citing'>entelein 20,12,2020</p>
      </div>
    </div>
  );
}

export default App;
