import './App.css';
import factsData from './WitzigeNilsFakten.json';
import { useState, useEffect } from 'react';

function App() {
  const backgroundStyle = {
    backgroundImage: `url('giphy.gif')`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh'
  };
  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Farbe des Overlays (halbtransparentes Schwarz)
  };
  const [facts, setFacts] = useState([]);
  useEffect (() => {
    if (factsData.fakten && factsData.fakten.length > 0) {
      setFacts(factsData.fakten[0]);}
  }, []);

  return (
    <div className="App" style={backgroundStyle}>
       <div style={overlayStyle}>
      <h1 id='titel' className='stiling'>Witziger Nilsfakt</h1>
      <div class='position'>
      {facts ? (
        <p class='stiling' id='quote'>
        {facts.inhalt}</p>,
        <p class='stiling' id='citing'>{facts.author} {facts.datum}</p>
        ) :("fdfs")
        }

      </div>
      </div>

    </div>
  );
}

export default App;
